import React, { Component } from 'react';
import Login from '../Login';
import Main from '../Main'

class App extends Component {
  constructor() {
    super();

    this.state = {
      username: "",
      loggedIn: false
    };
  }
  handleFormLogin = (username) => {
    console.log(username)
    this.setState({
      loggedIn: true,

    })
  }

  render() {
    return (
      <div className="App container">
      <div className="column is-half is-offset-one-quarter">
      {this.state.loggedIn ? <Main username={this.state.username}/> : <Login  onLogin={this.handleFormLogin}/>}

      </div>
      </div>


    );
  }
}

export default App;
